import { Component } from "react";

export default class TaskFilter extends Component {
  constructor() {
    super();
    this.clickButtonFilter = () => {
      this.setState({
        used: true,
      });
    };

    this.state = {
      used: false,
    };
  }
  render() {
    const { used } = this.state;

    let classNames = "";
    if (used) {
      classNames = "selected";
    }
    return (
      <ul className="filters">
        <li>
          <button className={classNames} onClick={this.clickButtonFilter}>
            All
          </button>
        </li>
        <li>
          <button onClick={this.clickButtonFilter}>Active</button>
        </li>
        <li>
          <button onClick={this.clickButtonFilter}>Completed</button>
        </li>
      </ul>
    );
  }
}

