import TaskFilter from "../task-filter";

const Footer = ({
  todoCount,
  onDeleteAllItem,
  onAllButton,
  onActiveButton,
  onCompletedButton,
}) => {
  return (
    <footer className="footer">
      <span className="todo-count">{todoCount} items left</span>
      <TaskFilter
        onAllButton={onAllButton}
        onActiveButton={onActiveButton}
        onCompletedButton={onCompletedButton}
      />
      <button className="clear-completed" onClick={onDeleteAllItem}>
        Clear completed
      </button>
    </footer>
  );
};

export default Footer;
