import { createRoot } from "react-dom/client";
import { Component } from "react";

import "./components/index.css";

import Footer from "./components/footer";
import TaskList from "./components/task-list";
import NewTaskForm from "./components/new-task-form";

class App extends Component {
  maxId = 100;

  state = {
    tododata: [
      this.createTodoItem("Drink cofee"),
      this.createTodoItem("do todo"),
      this.createTodoItem("111"),
    ],
    activebutton: "all",
  };

  addItem = (task) => {
    this.setState(({ tododata }) => {
      const element = this.createTodoItem(task);
      const newArray = [...tododata, element];
      return {
        tododata: newArray,
      };
    });
  };

  deleteItem = (id) => {
    this.setState(({ tododata }) => {
      const idx = tododata.findIndex((el) => el.id === id);
      const newArray = [...tododata.slice(0, idx), ...tododata.slice(idx + 1)];
      return {
        tododata: newArray,
      };
    });
  };

  deleteAllItem = () => {
    this.setState(({ tododata }) => {
      const newArray = [...tododata.slice(0, 0)];
      return {
        tododata: newArray,
      };
    });
  };

  onToggleDone = (id) => {
    this.setState(({ tododata }) => {
      const idx = tododata.findIndex((el) => el.id === id);
      const oldItem = tododata[idx];
      const newItem = { ...oldItem, done: !oldItem.done };

      const newArray = [
        ...tododata.slice(0, idx),
        newItem,
        ...tododata.slice(idx + 1),
      ];

      return {
        tododata: newArray,
      };
    });
  };

  createTodoItem(label) {
    return {
      label,
      done: false,
      id: this.maxId++,
      checked: false,
    };
  }
  filterButton(data, nameFilter) {
    switch (nameFilter) {
      case "all":
        return data;
      case "active":
        return data.filter((el) => !el.done);
      case "completed":
        return data.filter((el) => el.done);
      default:
        return data;
    }
  }
  render() {
    const { tododata, activebutton } = this.state;
    const doneCount = tododata.filter((el) => el.done).length;
    const todoCount = tododata.length - doneCount;

    const visibleItems = this.filterButton(tododata, activebutton);
    this.clickButtonFilter = (name) => {
      this.setState({ activebutton: name });
    };

    return (
      <section className="todoapp">
        <NewTaskForm onItemAdded={this.addItem} />
        <section className="main">
          <TaskList
            data={visibleItems}
            onDeleted={this.deleteItem}
            onToggleDone={this.onToggleDone}
          />
          <Footer
            todoCount={todoCount}
            onDeleteAllItem={this.deleteAllItem}
            clickButtonFilter={this.clickButtonFilter}
            activeButton={activebutton}
          />
        </section>
      </section>
    );
  }
}

const container = document.getElementById("root");

createRoot(container).render(<App />);
