import { Component } from "react";

export default class TaskFilter extends Component {
  render() {
    const buttons = { all: "All", active: "Active", completed: "Completed" };

    const ButtonFilter = buttons.map((el) => {
      return (
        <li>
          <button className="selected" onClick={onAllButton}>
            {el}
          </button>
        </li>
      );
    });
    const { onAllButton, onActiveButton, onCompletedButton } = this.props;

    return (
      <ul className="filters">
        <ButtonFilter />
      </ul>
    );
  }
}

