import { Component } from "react";

export default class TaskFilter extends Component {
  constructor() {
    super();
    this.clickButtonFilter = () => {
      console.log(buttonFilterName);
    };
  }
  render() {
    const classNames = "selected";
    const buttonFilterNames = ["All", "Active", "Completed"];
    const buttonFilterName = buttonFilterNames.map((el) => {
      return (
        <li>
          <button onClick={this.clickButtonFilter}>{el}</button>
        </li>
      );
    });

    return <ul className="filters">{buttonFilterName}</ul>;
  }
}

