import { createRoot } from "react-dom/client";
import { Component } from "react";

import "./components/index.css";

import Footer from "./components/footer";
import TaskList from "./components/task-list";

const searchPanel = document.querySelector(".new-todo");

let task;

class App extends Component {
  constructor() {
    super();

    this.state = {
      tododata: [
        { label: "Drink Coffee", important: false, id: 1 },
        { label: "Make Awesome App", important: true, id: 2 },
        { label: "Have a lunch", important: false, id: 3 },
      ],
    };
    this.deleteItem = (id) => {
      this.setState(({ tododata }) => {
        const idx = tododata.findIndex((el) => el.id === id);
        const newArray = [
          ...tododata.slice(0, idx),
          ...tododata.slice(idx + 1),
        ];
        return {
          tododata: newArray,
        };
      });
    };
    searchPanel.addEventListener("keydown", function (e) {
      if (e.keyCode === 13) {
        task = this.value;
        this.addItem(task);
      }
    });
  }

  addItem = (task) => {
    setState(({ tododata }) => {
      const element = { label: task, important: false, id: 4 };
      const newArray = [...tododata, element];
      return {
        tododata: newArray,
      };
    });
  };

  render() {
    return (
      <section className="main">
        <TaskList data={this.state.tododata} onDeleted={this.deleteItem} />
        <Footer />
      </section>
    );
  }
}

const container = document.getElementById("root");

console.log(<App />);

createRoot(container).render(<App />);
