export default class NewTaskForm extends Component {
  state = {
    label: "",
  };
  con = () => console.log(this.state.label);
  onLabelChandge = (e) => {
    this.setState(({ label }) => {
      return {
        label: e.target.value,
      };
    });
  };
  render() {
    return (
      <header className="header">
        <h1>todos</h1>
        <input
          className="new-todo"
          placeholder="What needs to be done?"
          autoFocus
          onChange={this.onLabelChandge}
        />
      </header>
    );
  }
}
