import { Component } from "react";
import { formatDistanceToNow } from "date-fns";

export default class Task extends Component {
  state = {
    label: this.props.label,
    id: this.props.id,
  };

  onLabelChandge = (e) => {
    this.setState({ label: e.target.value });
  };

  onSubmit = (e) => {
    e.preventDefault();
    this.props.onNewLabel(this.state.label, this.state.id);
    this.props.onRename(this.id);
    this.setState({ label: "" });
  };

  render() {
    const {
      label,
      onDeleted,
      onToggleDone,
      done,
      isChecked,
      onRename,
      isRename,
    } = this.props;

    const newDate = new Date().toLocaleTimeString();

    let classNames = "view";
    if (done) {
      classNames = "completed";
    }
    if (isRename) {
      return (
        <form className={classNames} onSubmit={this.onSubmit}>
          <input
            className="new-todo"
            autoFocus
            value={this.state.label}
            onChange={this.onLabelChandge}
          />
        </form>
      );
    } else {
      return (
        <div className={classNames}>
          <input
            className="toggle"
            type="checkbox"
            checked={isChecked}
            onChange={onToggleDone}
          />
          <label>
            <span className="description" onClick={onToggleDone}>
              {label}
            </span>
            <span className="created">{formatDistanceToNow(newDate)}</span>
          </label>
          <button className="icon icon-edit" onClick={onRename}></button>
          <button className="icon icon-destroy" onClick={onDeleted}></button>
        </div>
      );
    }
  }
}
