import { Component } from "react";

export default class TaskFilter extends Component {
  constructor() {
    super();
    this.clickButtonFilter = () => {
      this.setState({
        used: true,
      });
    };
    this.state = {
      used: false,
    };
  }
  render() {
    const { used } = this.state;

    let classNames = "";
    if (used) {
      classNames = "selected";
    }
    const buttonFilterNames = ["All", "Active", "Completed"];
    const buttonFilterName = buttonFilterNames.map((el, i) => {
      return (
        <li key={i}>
          <button onClick={this.clickButtonFilter.bind(this)}>{el}</button>
        </li>
      );
    });

    return <ul className="filters">{buttonFilterName}</ul>;
  }
}

